    private void GetRoleList()
    {
        rolesList = new List<Master_Role>();
        var AllRoles = _RoleManager.Roles.ToList();
        foreach (var item in AllRoles)
        {
            var x = new Master_Role();
            x.Id = item.Id;
            x.Role = item.Name;
            rolesList.Add(x);
        }
    }
